function editWordDocument($input, $output, $replacements){
    if(copy($input, $output)){
        $zip = new ZipArchive();

        if($zip->open($output, ZipArchive::CREATE) !== true){
            return false;
        }

        $xml = $zip->getFromName('word/document.xml');
        $xml = str_replace(array_keys($replacements), array_values($replacements), $xml);

        if(false === $zip->addFromString('word/document.xml', $xml)){
            return false;
        }
        $zip->close();
        return true;

    } return false;
}

function turnAnalystNamesArrayIntoString($leadAnalyst, $allAnalyst){
    $newStr = "";
    $newStr .= $leadAnalyst[0] . " " . $leadAnalyst[1] . ", ";
    foreach($allAnalyst as $analyst){
        $newStr .= $analyst[0] . " " . $analyst[1] . ", ";
    }

    return substr($newStr, 0, -2);
}

function generateTableListOfFindings($tableRow, $findings){
    $newTable = "";
    foreach($findings as $finding){

        $replacements  = [
            '{Finding-ID-1}'          => $finding[0],
            '{Finding-Description-1}' => $finding[1],
            'Likelihood-1'  => $finding[2],
            'Impact-1'      => $finding[3],
            'Risk-1'        => $finding[4],
            'FFC000'        => colorSelector($finding[4]),
            'FF0000'        => colorSelector($finding[3]),
            'FFBF8F'        => colorSelector($finding[2])
        ];

        $newTable .= str_replace(array_keys($replacements), array_values($replacements), $tableRow);
    }

    return $newTable;
}

function colorSelector($type){
    $type = strtolower($type);
    switch($type){
        case "vh":
            return "FF0001";
        case "h":
            return "FFC001";
        case "m":
            return "FFBF90";
        case "l":
            return "FFFF00";
        case "vl":
            return "92D050";
        case "info":
            return "0070C0";
        default:
            return "D9D9D9";        
    }
}
