function editWordDocument($input, $output, $replacements){
    if(copy($input, $output)){
        $zip = new ZipArchive();

        if($zip->open($output, ZipArchive::CREATE) !== true){
            return false;
        }

        $xml = $zip->getFromName('word/document.xml');
        $xml = str_replace(array_keys($replacements), array_values($replacements), $xml);

        if(false === $zip->addFromString('word/document.xml', $xml)){
            return false;
        }
        $zip->close();
        return true;

    } return false;
}

function turnAnalystNamesArrayIntoString($leadAnalyst, $allAnalyst){
    $newStr = "";
    $newStr .= $leadAnalyst[0] . " " . $leadAnalyst[1] . ", ";
    foreach($allAnalyst as $analyst){
        $newStr .= $analyst[0] . " " . $analyst[1] . ", ";
    }

    return substr($newStr, 0, -2);
}

function generateTableListOfFindings($tableRow, $findings){
    $newTable = "";
    foreach($findings as $finding){

        $replacements  = [
            '{Finding-ID-1}'          => $finding[0],
            '{Finding-Description-1}' => $finding[1],
            'Likelihood-1'            => $finding[2],
            'Impact-1'                => $finding[3],
            'Risk-1'                  => $finding[4],
            'FFC000'                  => colorSelector($finding[4]),
            'FF0000'                  => colorSelector($finding[3]),
            'FFBF8F'                  => colorSelector($finding[2])
        ];

        $newTable .= str_replace(array_keys($replacements), array_values($replacements), $tableRow);
    }

    return $newTable;
}

function generateFindingPages($pageTemplate, $findings){
    $newPage = "";
    foreach($findings as $finding){

        $replacements  = [
            'Finding-1'             => $finding[0],
            '{Current-Index-1}'     => $finding[1],
            'ID-1'                  => $finding[2],
            'Hostname-1'            => $finding[3],
            'IpPort-1'              => $finding[4],
            'Impact-Scr-1'          => colorSelector($finding[4]),
            'CAT-1'                 => colorSelector($finding[3]),
            'CAT-Scr-1'             => colorSelector($finding[2]),
            'VS-Scr-1'              => colorSelector($finding[2]),
            'VS-1'                  => colorSelector($finding[2]),
            'Status-1'              => colorSelector($finding[2]),
            'Likelihood-1'          => colorSelector($finding[2]),
            'Impact-1'              => colorSelector($finding[2]),
            'Risk-1'                => colorSelector($finding[2]),
            'CM-1'                  => colorSelector($finding[2]),
            'C-1'                   => colorSelector($finding[2]),
            'I-1'                   => colorSelector($finding[2]),
            'A-1'                   => colorSelector($finding[2]),
            'Posture-1'             => colorSelector($finding[2]),
            'Impact-Rationale-1'    => colorSelector($finding[2]),
            'Type-1'                => colorSelector($finding[2]),
            'Finding-Description-1' => colorSelector($finding[2]),
            'Finding-Mitigation-1'  => colorSelector($finding[2]),
            '{Index-1}'             => $finding[0]
        ];

        $newPage .= str_replace(array_keys($replacements), array_values($replacements), $pageTemplate);
    }

    return $newPage;
}

function colorSelector($type){
    $type = strtolower($type);
    switch($type){
        case "vh":
            return "FF0001";
        case "h":
            return "FFC001";
        case "m":
            return "FFBF90";
        case "l":
            return "FFFF00";
        case "vl":
            return "92D050";
        case "info":
            return "0070C0";
        default:
            return "D9D9D9";        
    }
}
